mapper.select:
/* goodsListMapper.selectGoodsList */ SELECT * FROM (SELECT ROWNUM as rnum,gdNo,gdGp,gdSp,gdNm,gdCnt,gdYn,gdPrice,gdImg,gdGpNm,gdSpNm FROM (SELECT a.GD_NO as gdNo ,a.GD_GP as gdGp ,a.GD_SP as gdSp ,a.GD_NM as gdNm ,a.GD_CNT as gdCnt ,a.GD_YN as gdYn ,a.GD_PRICE as gdPrice ,a.GD_IMG as gdImg ,b.cm_grnm as gdGpNm ,c.cm_grnm as gdSpNm FROM GOODS_INFO a left OUTER JOIN CMCD_MGT b ON b.CM_GRCD = a.GD_GP left OUTER JOIN CMCD_MGT c ON c.CM_GRCD = a.GD_SP and c.CM_UPCD = a.GD_GP WHERE 1 = 1
AND GD_REGDATE BETWEEN TO_DATE(#{startDt}) AND TO_DATE(#{endDt})+ 1
AND GD_GP IN ('bestBook','newBook','localBook','foreignBook')
AND GD_GP = #{selectOptValOne}
AND GD_SP IN ('foreign','child','magazine','it','exam','general','special')
AND GD_SP = #{selectOptValTwo}
AND(GD_NM LIKE '%'||#{searchVal}||'%' OR GD_NO LIKE '%'||#{searchVal}||'%')
AND(GD_NM LIKE '%'||#{searchVal}||'%')
AND(GD_NO LIKE '%'||#{searchVal}||'%')
ORDER BY GD_REGDATE DESC,GD_NO DESC )WHERE ROWNUM <= #{endCount} )WHERE rnum >= #{startCount}

/* goodsListMapper.selectGoodsSeparate */
SELECT *
  FROM CMCD_MGT
 WHERE CM_UPCD = #{goodsGroup}

mapper.update:
/* goodsListMapper.updateGoods */
UPDATE GOODS_INFO
   SET
    GD_GP = #{gdGp}
   ,GD_SP = #{gdSp}
   ,GD_NM = #{gdNm}
   ,GD_CNT = #{gdCnt}
   ,GD_PAGE = #{gdPage}
   ,GD_THICK = #{gdThick}
   ,GD_WR = #{gdWr}
   ,GD_PB = #{gdPb}
   ,GD_DC = #{gdDc}
   ,GD_YN = #{gdYn}
   ,GD_PRICE = #{gdPrice}

   ,GD_IMG = #{gdImg}


   ,GD_DETL = #{gdDetl}
   ,GD_DETLNM = #{gdDetlNm}

WHERE GD_NO = #{gdNo}

mapper.delete:
/* goodsListMapper.deleteGoods */
DELETE 
  FROM GOODS_INFO
 WHERE GD_NO IN

	#{item}

mapper.update:
	/* goodsListMapper.showGoodsTrue */
	UPDATE GOODS_INFO
	   SET GD_YN = 'Y'
     WHERE GD_NO IN

		#{item}

	/* goodsListMapper.showGoodsFalse */
	UPDATE GOODS_INFO
	   SET GD_YN = 'N'
     WHERE GD_NO IN

		#{item}

mapper.select:
/* goodsListMapper.selectGoodsListCount */ SELECT COUNT(*) FROM GOODS_INFO WHERE 1 = 1
AND GD_REGDATE BETWEEN TO_DATE(#{startDt}) AND TO_DATE(#{endDt})+ 1
AND GD_GP IN ('bestBook','newBook','localBook','foreignBook')
AND GD_GP = #{selectOptValOne}
AND GD_SP IN ('foreign','child','magazine','it','exam','general','special')
AND GD_SP = #{selectOptValTwo}
AND(GD_NM LIKE '%'||#{searchVal}||'%' OR GD_NO LIKE '%'||#{searchVal}||'%')
AND(GD_NM LIKE '%'||#{searchVal}||'%')
AND(GD_NO LIKE '%'||#{searchVal}||'%')
