mapper.insert:
/* JoinMapper.doJoin */
	INSERT INTO CUSTOMER_INFO(
		 CS_NO 
		,CS_GRADE 
		,CS_ID 
		,CS_PS 
		,CS_NM 
		,CS_ADDR_ONE 
		,CS_ADDR_TWO 
		,CS_BIRTH_YEAR 
		,CS_BIRTH_MONTH 
		,CS_BIRTH_DAY 
		,CS_PHONE 
		,CS_EMAIL 
		,CREATE_DATE 
	)
	VALUES(
		CUSTOMER_INFO_SEQ.nextval
		,'1'
		,#{csId}
		,#{csPs}
		,#{csNm}
		,#{csAddrOne}
		,#{csAddrTwo}
		,#{csBirthYear}
		,#{csBirthMonth}
		,#{csBirthDay}
		,#{csPhone}
		,#{csEmail}
		,SYSDATE
	)

mapper.select:


/* JoinMapper.idCheck */
	SELECT COUNT(*) 
	  FROM CUSTOMER_INFO
	 WHERE CS_ID = #{csId}

/* JoinMapper.emailChk */
	SELECT COUNT(*)
	  FROM CUSTOMER_INFO
	 WHERE CS_EMAIL = #{csEmail}
