mapper.insert:
/*  menuMgtMapper.setMenuMgt */
	INSERT INTO MENU_MGT
	(  MU_ID
	 , MU_NM
	 , MU_CD
	 , MU_UPCD
	 , MU_SN
	 , ADMIN_YN
	 , USER_YN
	 )
	VALUES
	(TO_CHAR(MENU_MGT_SEQ.NEXTVAL)
	,#{menuNm}
	,#{menuCd}
	,#{menuUpCd}
	,#{menuSn}
	,#{adminYn}
	,#{userYn}
	)

mapper.update:
/*  menuMgtMapper.updateMenuMgt */
	UPDATE MENU_MGT
	   SET
	    MU_NM = #{menuNm}, 
	    MU_CD = #{menuCd},
	    MU_UPCD = #{menuUpCd},
	    MU_SN = #{menuSn},
	    ADMIN_YN = #{adminYn},
	    USER_YN = #{userYn}
	 WHERE MU_ID =#{menuId}

mapper.delete:
/*  menuMgtMapper.deleteMenuMgt */
	DELETE
	FROM MENU_MGT
	WHERE MU_ID = #{menuId}
