mapper.select:
/* myCartMapper.cartCount */
	SELECT COUNT(*)
	  FROM CART
	 WHERE CS_NO = #{csNo}
	   AND GD_NO = #{gdNo}

mapper.insert:
/* myCartMapper.insertCart */
INSERT INTO CART
	 VALUES (
	 		CART_SEQ.nextval
	 	 	,#{csNo}
	 		,#{gdNo}
	 )

mapper.update:
/* myCartMapper.updateCart */
	UPDATE CART
	   SET GD_NO = #{gdNo}
	 WHERE CS_NO = #{csNo}
