mapper.select:
/* myOrderMapper.selectMyOrderInfo */ SELECT odNo,odState,odDate,gdNm FROM (SELECT ROWNUM as rnum,odNo,odState,odDate,gdNm FROM (SELECT o.OD_NO AS odNo ,DECODE(o.OD_STATE,'100','주문완료','200','주문취소','300','배송중','400','배송완료') AS odState ,o.OD_DATE AS odDate ,LISTAGG(g.GD_NM,',') AS gdNm FROM ORDER_INFO o LEFT OUTER JOIN goods_info g ON g.GD_NO = o.GD_NO WHERE o.CS_NO = #{selectOptValThree}
AND o.OD_DATE BETWEEN TO_DATE(#{startDt}) AND TO_DATE(#{endDt})+ 1
AND o.OD_STATE IN ('100','200','300','400')
AND o.OD_STATE = #{selectOptValOne}
AND(o.OD_NO LIKE '%'||#{searchVal}||'%' OR g.GD_NM LIKE '%'||#{searchVal}||'%')
AND(o.OD_NO LIKE '%'||#{searchVal}||'%')
AND(g.GD_NM LIKE '%'||#{searchVal}||'%')
GROUP BY o.OD_NO, o.OD_STATE, o.OD_DATE ORDER BY o.OD_DATE )WHERE ROWNUM <= #{endCount} )WHERE rnum >= #{startCount}

/* myOrderMapper.selectMyOrderListCount */ select count(odNo) from (SELECT o.OD_NO AS odNo ,o.OD_STATE AS odState ,o.OD_DATE AS odDate ,LISTAGG(g.GD_NM,',') AS gdNm FROM ORDER_INFO o LEFT OUTER JOIN goods_info g ON g.GD_NO = o.GD_NO WHERE o.CS_NO = #{selectOptValThree}
AND o.OD_DATE BETWEEN TO_DATE(#{startDt}) AND TO_DATE(#{endDt})+ 1
AND o.OD_STATE IN ('100','200','300','400')
AND o.OD_STATE = #{selectOptValOne}
AND(o.OD_NO LIKE '%'||#{searchVal}||'%' OR g.GD_NM LIKE '%'||#{searchVal}||'%')
AND(o.OD_NO LIKE '%'||#{searchVal}||'%')
AND(g.GD_NM LIKE '%'||#{searchVal}||'%')
GROUP BY o.OD_NO, o.OD_STATE, o.OD_DATE)
