mapper.select:
/* orderListMapper.selectMyOrderList */ SELECT csId ,csNm ,odNo ,odDate ,odState ,trDate FROM (SELECT ROWNUM as rnum ,csId ,csNm ,odNo ,odDate ,odState ,trDate FROM (SELECT O.OD_NO AS odNo ,DECODE(O.OD_STATE,'100','주문완료','200','주문취소','300','배송중','400','배송완료') AS odState ,TO_CHAR(O.OD_DATE,'YYYY-MM-DD HH24:MI') AS odDate ,DECODE(T.TR_DATE,null,'-',TO_CHAR(T.TR_DATE,'YYYY-MM-DD HH24:MI')) AS trDate ,C.CS_ID AS csId ,C.CS_NM AS csNm FROM ORDER_INFO O LEFT OUTER JOIN TRANSFER_INFO T ON T.OD_NO = O.OD_NO LEFT OUTER JOIN CUSTOMER_INFO C ON C.CS_NO = O.CS_NO WHERE 1=1
AND O.OD_DATE BETWEEN TO_DATE(#{startDt}) AND TO_DATE(#{endDt})+ 1
AND T.TR_DATE BETWEEN TO_DATE(#{startDt}) AND TO_DATE(#{endDt})+ 1
AND O.OD_STATE IN ('100','200','300','400')
AND O.OD_STATE = #{selectOptValOne}
AND( O.OD_NO LIKE '%'||#{searchVal}||'%' OR C.CS_ID LIKE '%'||#{searchVal}||'%' OR C.CS_NM LIKE '%'||#{searchVal}||'%')
AND(O.OD_NO LIKE '%'||#{searchVal}||'%')
AND(C.CS_ID LIKE '%'||#{searchVal}||'%')
AND(C.CS_NM LIKE '%'||#{searchVal}||'%')
GROUP BY O.OD_NO, O.OD_STATE, O.OD_DATE, T.TR_DATE, C.CS_ID, C.CS_NM ORDER BY O.OD_DATE DESC )WHERE ROWNUM <= #{endCount} )WHERE rnum >= #{startCount}

mapper.update:
/*  orderListMapper.updateOdState */
UPDATE ORDER_INFO

   SET OD_STATE = '200'


   SET OD_STATE = '300'

 WHERE OD_NO = #{checkedOdNo}

mapper.select:
/*  orderListMapper.hasOdNo */
SELECT
	COUNT(OD_NO)
  FROM TRANSFER_INFO
 WHERE OD_NO = #{checkedOdNo}

mapper.insert:
/*  orderListMapper.insertTrDate */
INSERT INTO TRANSFER_INFO (TR_NO, TR_DATE, OD_NO)
VALUES (TRANSFER_INFO_SEQ.NEXTVAL, SYSDATE ,#{checkedOdNo})
