mapper.select:
/* newBookMapper.getNbList */ SELECT * FROM (SELECT ROWNUM as rnum,gdNo,gdGp,gdSp,gdNm,gdCnt,gdYn,gdPrice,gdImg,gdGpNm,gdSpNm,gdWr,gdPb,gdDc FROM (SELECT a.GD_NO as gdNo ,a.GD_GP as gdGp ,a.GD_SP as gdSp ,a.GD_NM as gdNm ,a.GD_CNT as gdCnt ,a.GD_PAGE as gdPage ,a.GD_THICK as gdThick ,a.GD_WR as gdWr ,a.GD_PB as gdPb ,a.GD_DC as gdDc ,a.GD_YN as gdYn ,a.GD_IMG as gdImg ,a.GD_PRICE as gdPrice ,b.cm_grnm as gdGpNm ,c.cm_grnm as gdSpNm FROM GOODS_INFO a left OUTER JOIN CMCD_MGT b ON b.CM_GRCD = a.GD_GP left OUTER JOIN CMCD_MGT c ON c.CM_GRCD = a.GD_SP and c.CM_UPCD = a.GD_GP WHERE 1 = 1 AND GD_YN = 'Y' AND GD_GP = 'newBook'
AND GD_SP IN ('special','general','foreign','child','magazine','it','exam')
AND GD_SP = #{selectOptValTwo}
AND(GD_NM LIKE '%'||#{searchVal}||'%' OR GD_NO LIKE '%'||#{searchVal}||'%')
AND(GD_NM LIKE '%'||#{searchVal}||'%')
ORDER BY GD_REGDATE DESC,GD_NO DESC )WHERE ROWNUM <= #{endCount} )WHERE rnum >= #{startCount}

/* newBookMapper.getNbListCount */ SELECT COUNT(*) FROM GOODS_INFO WHERE 1 = 1 AND GD_YN = 'Y' AND GD_GP = 'newBook'
AND GD_SP IN ('foreign','child','magazine','it','exam','general','special')
AND GD_SP = #{selectOptValTwo}
AND(GD_NM LIKE '%'||#{searchVal}||'%' OR GD_NO LIKE '%'||#{searchVal}||'%')
AND(GD_NM LIKE '%'||#{searchVal}||'%')
